/* eslint-disable import/no-anonymous-default-export */
import React, { useState } from 'react';
import { InputGroup, FormControl, Button, Form, Col, Row } from 'react-bootstrap';

export default (props) => {

    const [name, setName] = useState('');
    const [image, setImage] = useState('');
    const [isAlive, setIsAlive] = useState(false);

    const submit = (ev) => {
        ev.preventDefault();
        if (name && image) {
          props.onAddVillain({
            name,
            image,
          });
        }
    }

    return <Form onSubmit={submit}>
          <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
            <Form.Label column sm="2">
              Name
            </Form.Label>
            <Col sm="6">
              <FormControl onChange={(ev) => setName(ev.target.value)} placeholder="Villain's name" value={name} />
            </Col>
            <InputGroup className="mb-3">
            <Form.Label column sm="2">
              Image URL
            </Form.Label>
            <Col sm="6">
              <FormControl onChange={(ev) => setName(ev.target.value)} placeholder="Villain's image URL" value={image} />
            </Col>
            </InputGroup>
          </Form.Group>
                <Button type="submit">Add</Button>
        </Form>;
}
